group <- c(group_input, group_ref)

# Run ComBat_Seq in chunks
chunk_size <- 5000
num_rows <- nrow(count_matrix)
num_chunks <- ceiling(num_rows/chunk_size)
res_list <- vector("list", num_chunks)
for (i in seq_len(num_chunks)) {
  start_row <- ((i-1) * chunk_size) + 1
  end_row <- min(i * chunk_size, num_rows)
  cat("Processing rows:", start_row, "to", end_row, "\n")

  count_subset <- count_matrix[start_row:end_row, ]
  res <- ComBat_seq(count_subset, batch=batch, group=group)
  res_list[[i]] <- res

  rm(count_subset, res)
  gc()
}

corrected_data <- do.call(rbind, res_list)
rm(res_list)
gc()

# Combine corrected data with original metadata
final_matrix <- cbind(data_frame[, 1:3], corrected_data)
write.table(final_matrix, file = gzfile(output_path), sep = "\t", quote = FALSE, row.names = FALSE)

rm(corrected_data, final_matrix)
gc()


